    /** Uses the Final Fantasy XIV logo as the icon for a button, and the game name as the default button text.
     * 
     * @param props The props to pass into the button component for a specific instance.
     * @returns The formatted button component.
     */
    public static FFXIVButton ( props: ButtonProps ): ReactElement {
        return (
            <Button px={Icons.defaultButtonPaddingX} h={Icons.defaultButtonHeight} {...props} leftIcon={<Icons.FFXIV />}>
                {props.children ? props.children : "Final Fantasy XIV"}
            </Button>
        );
    }

